#Hidden Markov Model initialization
Markov <- initHMM(States = AllStates, Symbols = AminoAcidSymbols, transProbs = TransitionMatrix, startProbs = NULL, emissionProbs = NULL)


#Loading Data to data frame

dataFrame <- fromJSON("Data/jsonProteins.json", flatten = TRUE)

#Baum Welch training
train <- function(model, dataFrame) {
    for (i in 1:nrow(dataFrame)) {
        model <- baumWelch(hmm = model, observation = unlist(strsplit(dataFrame[i, 3], '')), maxIterations = 2)
        }

    return(model)
}



